mapper.select:
   		 select * from board_table


		            WHERE boardWriter like CONCAT('%', #{searchName}, '%')
		            ORDER BY id DESC
		            LIMIT #{pageStart}, #{perPageNum}


		            WHERE boardTitle like CONCAT('%', #{searchName}, '%')
		            ORDER BY id DESC
		            LIMIT #{pageStart}, #{perPageNum}


		            ORDER BY id DESC
		            LIMIT #{pageStart}, #{perPageNum}
